con:config.script:
// Import the Groovy class required to work with SQL databases.
import groovy.sql.Sql

// Set up database connection properties.
/* IMPORTANT: must start with jdbc:mysql:// */
def url = 'jdbc:mysql://db4free.net:3306/soapuitesting'
def user = 'soapuiuser'
def password = 'soapuipassword'
def driver = 'com.mysql.jdbc.Driver'

// Register the MySQL JDBC driver â€“ required for Groovy to send requests to the database.
com.eviware.soapui.support.GroovyUtils.registerJdbcDriver( driver )

// Connect to the SQL instance.
def sql = Sql.newInstance(url, user, password, driver)

// Use the SQL instance.
sql.eachRow('SELECT * FROM pet') { row -> 
    log.info row[0]
}

// Close the SQL instance.
sql.close()
